mapper.select:
SELECT * FROM MEMBER
WHERE MEMBER_ID = #{memberId}

AND MEMBER_STATUS = 'Y'

mapper.delete:
UPDATE MEMBER 
SET MEMBER_STATUS ='N'
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT COUNT(*) 
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_PWD FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_STATUS = 'Y'
