mapper.update:
UPDATE SPACE SET SPACE_COMMENT = #{spaceComment}, SPACE_DETAIL = #{spaceDetail}, SPACE_TAG = #{spaceTag}, SPACE_OPTION = #{spaceOption}, SPACE_NOTICE = #{spaceNotice}, SPACE_TIME = #{spaceTime}, SPACE_MIN_PER = #{spaceMinPer}, SPACE_MAX_PER = #{spaceMaxPer}, SPACE_OPEN_TIME = #{spaceOpenTime}, SPACE_CLOSE_TIME = #{spaceCloseTime},
SPACE_NAME = #{spaceName}, SPACE_ADDRESS = #{spaceAddress}, TYPE_ID = #{typeId},
SPACE_PHONE = #{spacePhone}, SPACE_UPDATE = SYSDATE WHERE SPACE_ID = #{spaceId}

mapper.select:
SELECT SPACE_ATT_ID
FROM   SPACE_ATT
WHERE  SPACE_ATT_CHANGE = #{prevFileName}

mapper.update:
UPDATE SPACE_ATT
SET    SPACE_ATT_ORIGIN = #{spaceAttOrigin},
	   SPACE_ATT_CHANGE = #{spaceAttChange},
	   SPACE_ATT_FILEPATH = #{spaceAttChange}
WHERE  SPACE_ATT_ID = #{spaceAttId}

mapper.delete:
DELETE FROM SPACE_ATT
WHERE  SPACE_ID = #{spaceId}
AND	   SPACE_ATT_CHANGE = #{spaceAttChange}

mapper.update:
UPDATE PRICE
SET    PRICE_TIME = #{priceTime}
WHERE  PRICE_ID = #{priceId}
AND    SPACE_ID = #{spaceId}

mapper.delete:
DELETE SPACE
WHERE  SPACE_ID = #{spaceId}
AND    STATUS_ID ^= 2

mapper.select:
SELECT * 
FROM SPACE 
WHERE SPACE_ID =#{spaceId}

SELECT TYPE_NAME
FROM TYPE
WHERE TYPE_ID=#{typeId}

SELECT COUNT(*) 
FROM WISHLIST
WHERE MEMBER_ID=#{memberId}
AND SPACE_ID=#{spaceId}

mapper.delete:
DELETE 
FROM WISHLIST
WHERE MEMBER_ID=#{memberId}
AND SPACE_ID=#{spaceId}

mapper.select:
SELECT SPACE_ATT_CHANGE
FROM SPACE_ATT
WHERE SPACE_ID=#{spaceId}

SELECT PRICE_TIME
FROM PRICE
WHERE price_weekend=#{priceWeekend}
AND space_id=#{spaceId}

	SELECT DAYOFF_START
    FROM DAYOFF
    WHERE SPACE_ID=#{spaceId}

	 SELECT BOOK_START_TIME, BOOK_END_TIME
     FROM BOOK
     WHERE SPACE_ID=#{spaceId}
     AND BOOK_DATE=#{bookDate}
     AND STATUS_ID!=102
     AND BOOK_CANCEL IS NULL
